const arrows=document.querySelectorAll(".arrow"),movieList=document.querySelectorAll(".movie-list");arrows.forEach(((e,t)=>{const l=movieList[t].querySelectorAll("img").length;let o=0;e.addEventListener("click",(()=>{const e=Math.floor(window.innerWidth/270);o++,l-(4+o)+(4-e)>=0?movieList[t].style.transform=`translateX(${movieList[t].computedStyleMap().get("transform")[0].x.value-300}px)`:(movieList[t].style.transform="translateX(0)",o=0)}))}));const ball=document.querySelector(".toggle-ball"),items=document.querySelectorAll(".container,.movie-list-title,.navbar-container,.sidebar,.left-menu-icon,.toggle, .arrow");ball.addEventListener("click",(()=>{items.forEach((e=>{e.classList.toggle("active")})),ball.classList.toggle("active")}));